import { Component, OnDestroy, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Subscription } from 'rxjs';
import { CategoryService } from '../services/category.service';
import { Category } from '../models/category.model';
import { UpdateCategoryRequest } from '../models/update-category-request.model';

@Component({
  selector: 'app-edit-category',
  templateUrl: './edit-category.component.html',
  styleUrls: ['./edit-category.component.css']
})
export class EditCategoryComponent implements OnInit,OnDestroy {

  id: string | null= null;
paramsSubscription?: Subscription;
editCaegorySubscription?: Subscription;
category?: Category;

  constructor(private route: ActivatedRoute,
    private categoryService: CategoryService,
    private router: Router){

  }


  ngOnInit(): void {
    this.paramsSubscription = this.route.paramMap.subscribe({
      next: (params) => {
        this.id = params.get('id');

        if(this.id)
        {
          //get the data from the API for this category ID
          this.categoryService.getCategoryById(this.id)
          .subscribe({
            next: (response) => {
              this.category = response;

            }
          })
        }
      }
    });
  }
  // onFormSubmit(): void{
  //   console.log(this.category
  //     );
      onFormSubmit(): void{
        const updateCategoryRequest : UpdateCategoryRequest = {
          name: this.category?.name ?? '',
          urlHandle: this.category?.urlHandle ??''
        };

        // pass this object to service
        if (this.id){
        this.editCaegorySubscription = this.categoryService.updateCategory(this.id, updateCategoryRequest)
          .subscribe({
            next: (response) => {
              this.router.navigateByUrl('/admin/category');
            }
          });
      }
  }
  ngOnDestroy(): void {
    this.paramsSubscription?.unsubscribe();
    this.editCaegorySubscription?.unsubscribe();
  }
}
<div class="container">

    <h1 class="mt-3">Edit Category</h1>

    <ng-container *ngIf="category;else notFound">

        <form #form="ngForm" (ngSubmit)="onFormSubmit()">
            <div class="mt-2">
                <div class="mt-3">
                    <label class="form-label" for="categoryId">Id</label>
                    <input type="text" disabled
                    id="categoryId"
                    class="form-control"
                    name="categoryId"
                    value="{{category.id}}">
                </div>

                <div class="mt-3">
                    <label class="form-label" for="categoryName">Name</label>
                    <input type="text" 
                    id="categoryName"
                    class="form-control"
                    name="categoryName"

                    [(ngModel)]="category.name">
                </div>

                <div class="mt-3">
                    <label class="form-label" for="categoryUrlHandle">UrlHandle</label>
                    <input type="text" 
                    id="categoryUrlHandle"
                    class="form-control"
                    name="categoryUrlHandle"
                    [(ngModel)]="category.urlHandle">
                </div>

            <div class="mt-3">
                <button type="submit" class="btn btn-primary">Save</button>

            </div>

            </div>
        </form>

    </ng-container>

    <ng-template #notFound>
        <div class="alert alert-warning" role="alert">
            Category not found!
        </div>
    </ng-template>

</div>
